package com.si.powerlogimport org.apache.log4j.WriterAppenderimport org.apache.log4j.Layout/**  * 基于 AsyncContext 支持的 Appender  */  public class WebLogAppender extends WriterAppender {       /**      * CometD Writer      */      private Writer writer = new CometDWriter();      public WebLogAppender() {          setWriter(writer);      }      public WebLogAppender(Layout layout) {          this();          super.layout = layout;      }  }   